const { ADD_FAVORITE, DELETE_FAVORITE } = require('./types.js')

const initialState = {
  myFavorites: []
}

const rootReducer = (state = initialState, { type, payload }) => {
  switch (type){
    case 'ADD_FAVORITE':
      return {
        ...state,
        myFavorites: payload
      }

    case 'DELETE_FAVORITE':
      return {
        ...state,
        myFavorites: state.myFavorites.filter((c) => c.id !== payload)
      }

    default:
      return state
  }
} 

export default rootReducer
