const { ADD_FAVORITE, DELETE_FAVORITE, FILTER } = require('./types.js')

const initialState = {
  myFavorites: [],
  allCharacters: []
}

const rootReducer = (state = initialState, action) => {
  switch (action.type){
    case 'ADD_FAVORITE':
      return {
        ...state,
        myFavorites: [...state.myFavorites, action.payload],
        allCharacters: [...state.allCharacters, action.payload]
      }

    case 'DELETE_FAVORITE':
      return {
        ...state,
        myFavorites: state.myFavorites.filter((c) => c.id !== action.payload)
      }

    case 'FILTER':
      const copyAllC = [...state.allCharacters]
      return{
        ...state,
        myFavorites: copyAllC.filter((c) => c.gender === payload) 
      }

    default:
      return state
  }
} 

export default rootReducer
