// eslint-disable-next-line
const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

export function validate(username, password){
  let errors = {}

  if(!regexEmail.test(username) || !username || username.length> 35) errors.username = 'Debe ser un correo electrónico válido'

  if(password < 5 || password > 11) errors.password = 'Debe poner un password válido' 

  return errors
}
